-- Philippines-compliant Deductions System
-- SSS, PhilHealth, Pag-IBIG, and BIR tax calculations

-- SSS Contribution table
CREATE TABLE sss_contributions (
  id                    INTEGER PRIMARY KEY AUTOINCREMENT,
  salary_range_min      REAL NOT NULL,
  salary_range_max      REAL NOT NULL,
  employee_contribution REAL NOT NULL,
  employer_contribution REAL NOT NULL,
  total_contribution    REAL NOT NULL,
  effective_date        TEXT NOT NULL,
  created_at            TEXT NOT NULL DEFAULT (datetime('now'))
);

