  salary_range_min      REAL NOT NULL,
  salary_range_max      REAL NOT NULL,
  monthly_premium       REAL NOT NULL,
  effective_date        TEXT NOT NULL,
  created_at            TEXT NOT NULL DEFAULT (datetime('now'))
